def get_countries(self, region: Optional[str] = None, income_level: Optional[str] = None) -> Dict:
    endpoint = "countries"
    params = {}

    if region:
        endpoint = f"region/{region}/countries"
    if income_level:
        params['incomelevel'] = income_level

    return self._make_request(endpoint, params)

def get_indicators(self, topic: Optional[int] = None, source: Optional[int] = None) -> Dict:
    endpoint = "indicators"

    if topic:
        endpoint = f"topic/{topic}/indicators"
    elif source:
        endpoint = f"source/{source}/indicators"

    return self._make_request(endpoint)

def get_indicator_data(self, countries: Union[str, List[str]], indicator: str,
                      start_year: Optional[int] = None, end_year: Optional[int] = None,
                      mrv: Optional[int] = None) -> Dict:
    if isinstance(countries, list):
        countries = ';'.join(countries)

    endpoint = f"country/{countries}/indicator/{indicator}"

    params = {}
    if start_year and end_year:
        params['date'] = f"{start_year}:{end_year}"
    elif mrv:
        params['mrv'] = mrv

    return self._make_request(endpoint, params)

def search_indicators(self, query: str) -> Dict:
    params = {'q': query}
    return self._make_request("indicators", params)
